import './App.css';
import TopLocations from './components/TopLocations';
import Inputs from './components/Inputs';
import LocationTime from './components/LocationTime';
import TempDetails from './components/TempDetails';
import Forecast from './components/Forecast';
import getFormattedWeatherData from './services/weatherService';
import { useEffect, useState } from 'react';

function App() {

  const [query, setQuery] = useState({q: 'berlin'})
  const [units, setUnits] = useState('metric')
  const [weather, setWeather] = useState(null)

  useEffect (() => {
    const fetchWeather = async () => {
      const data = await getFormattedWeatherData(...query, units).then(data => {
        setWeather
      });
    };

    fetchWeather();
  }, [query, units])

  return (
    <div className="mx-auto max-w-screen-md mt-4 py-5 px-32 bg-gradient-to-br from-cyan-700 to-blue-700 hfit shadow-xl shadow-gray-400" >
      <TopLocations />
      <Inputs />
      <LocationTime />
      <TempDetails />

      <Forecast title="hourly forecast" />
      <Forecast title="daily forecast" /> 
    </div>
  );
}

export default App;
